<?php
/*******************************************************************************************************************/
/*                                              Se define la clase                                                 */
/*******************************************************************************************************************/
class FunctionsDataTime {

	/*******************************************************************************************************************/
	/*                                                                                                                 */
	/*                                                  Metodos                                                        */
	/*                                                                                                                 */
	/*******************************************************************************************************************/
	/************************************************************************************************************/
	public function formatoHoraEstandar($Hora): string{
		/*
		*=================================================     Detalles    =================================================
		*
		* Formatea la hora ingresada al formato estandar, solo horas y minutos
		*
		*=================================================    Modo de uso  =================================================
		*
		* 	//se formatea la hora
		* 	$DataTime->formatoHoraEstandar('1:1'); //devuelve 01:01
		*
		*=================================================    Parametros   =================================================
		* @input   time     $Hora   Hora a formatear
		* @return  time
		*===================================================================================================================
		*/

		/**********************  Validaciones   **********************/
		if($Hora=='00:00:00'){  return 'Sin Hora';}

		/********************** Si todo esta ok **********************/
		/**********************  Retorno datos  **********************/
		return date_format(date_create($Hora), 'H:i');

	}

	/************************************************************************************************************/
	public function formatoHoraProgramada($Hora): string{
		/*
		*=================================================     Detalles    =================================================
		*
		* Formatea la hora ingresada al formato de hora programada. horas minutos y segundos
		*
		*=================================================    Modo de uso  =================================================
		*
		* 	//se formatea la hora
		* 	$DataTime->formatoHoraProgramada('1:1'); //devuelve 01:01:00
		*
		*=================================================    Parametros   =================================================
		* @input   time     $Hora   Hora a formatear
		* @return  time
		*===================================================================================================================
		*/

		/**********************  Validaciones   **********************/
		//if(!$this->DataValidations->validarHora($Hora)){ return 'El dato ingresado no es una hora ('.$Hora.')';}
		if($Hora=='00:00:00'){  return 'Sin Hora';}

		/********************** Si todo esta ok **********************/
		$HoraForm = new DateTime($Hora);

		/**********************  Retorno datos  **********************/
		return $HoraForm->format('H:i:s');

	}

	/************************************************************************************************************/
	public function formatoHoraArchivos($Hora): string{
		/*
		*=================================================     Detalles    =================================================
		*
		* Formatea la hora ingresada al formato de hora programada, sin los dos puntos
		*
		*=================================================    Modo de uso  =================================================
		*
		* 	//se formatea la hora
		* 	$DataTime->formatoHoraArchivos('1:1'); //devuelve 010100
		*
		*=================================================    Parametros   =================================================
		* @input   time     $Hora   Hora a formatear
		* @return  time
		*===================================================================================================================
		*/

		/**********************  Validaciones   **********************/
		if($Hora=='00:00:00'){  return 'Sin Hora';}

		/********************** Si todo esta ok **********************/
		$HoraForm = new DateTime($Hora);

		/**********************  Retorno datos  **********************/
		return $HoraForm->format('His');

	}

}
